import React, {useState} from 'react';

import './ExpenseForm.css';

const ExpanseForm = () => {

//    const [enteredTitle, setEnteredTitele] = useState('');
//    const [enteredAmount, setEnteredAmount] = useState('');
//    const [enteredDate, setEnteredDate] = useState('');

   const [userInput, setUserIput] = useState({
    enteredTitle: '',
    enteredAmount: '',
    enteredDate: '',
   });

    const titeleChangeHandler = event => {
    //    setEnteredTitele(event.target.value);

    //    setUserIput({
    //     ...userInput,
    //     enteredTitle: event.target.value,
    //    });
    setUserIput(() => {});
    }

    const amountChangeHandler = event => {
        // setEnteredAmount(event.target.value);

        setUserIput({
            ...userInput,
            enteredAmount: event.target.value,
           });
    }

    const dateChangeHandler = event => {
        // setEnteredDate(event.target.value);

        setUserIput({
            ...userInput,
            enteredDate: event.target.value,
           });
    };

    return (
        <form action="">
            <div className="new-expense__controls">
            <div className="new-expense__controls">
            <label >Title</label>
            <input type="text" onChange={titeleChangeHandler}/>
            </div>
            <div className="new-expense__controls">
            <label htmlFor="">Amount</label>
            <input type="number" min="0.01" step="0.01" onChange={amountChangeHandler}/>
            </div>
            <div className="new-expense__controls">
            <label htmlFor="">Date</label>
            <input type="date" min="2019-01-01" step="2023-12-31" onChange={dateChangeHandler}/>
            </div>
            </div>
            <div className="new-expense__actions">
                <button type='submit'>Add Expense</button>
            </div>

        </form>
    )
};

export default ExpanseForm;
