import React, {useState} from 'react';

import ExpanseForm from './ExpenseForm';
import './NewExpense.css';

const NewExpense = (props) => {
 const [openForm, setOpenForm] = useState(false);

 const openExpenseFormHandler = () => {
    setOpenForm(true);
 }

 const closeExpenseForm = () => {
   setOpenForm(false);
 }

const saveExpenseDataHandler = (enteredExpenseData) => {
 const expenseData = {
    ...enteredExpenseData,
    id: Math.random().toString()
 }
 props.onAddExpanse(expenseData);
 console.log(expenseData);
};

    return (
        <div className="new-expense">
<ExpanseForm  onSaveExpenseData={saveExpenseDataHandler}/>
<button onClick={!openExpenseFormHandler === OpenForm} >Add New Expense</button>
        </div>
    )
}

export default NewExpense;
